import React from "react";
import HorendBeastC from "./HornedBeastFi";
import data from "./data.json";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import SelectedBeast from "./SelectedBeast";
import Form from "react-bootstrap/Form";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.setState = {
      // allBeast: data,
      // selectedBeast: {},
      // showModal: false,
      filterBeast: 0,
      // beast: "",
    };
  }

  handelFilterData = (e) => {
    e.preventDefault();
  };

  // displayModal = (beast) => {
  //   let result = this.state.allBeast.find((item) => item.title === beast.title);
  //   this.setState({
  //     showModal: true,
  //     selectedBeast: result,
  //   });
  // };

  // handleClose = () => {
  //   this.setState({
  //     showModal: false,
  //     selectedBeast: data,
  //   });
  // };

  handelChange = (event) => {
    this.setState({
      filterBeast: event.target.value,
    });
  };
  render() {
    let finalFilter = [];
    if (this.state.filterBeast === 0) {
      console.log(this.state.filterBeast);
      finalFilter = data;
    } else {
      finalFilter = data.filter((card) => card.horns == this.state.filterBeast);
    }
    return (
      <>
        <Form onSubmit={this.handelFilterData}>
          <Form.Group controlId="formGridState">
            <Form.Label htmlFor="numberOfHoren">
              select the horned beast you want to show
            </Form.Label>
            <Form.Select
              onChange={(e) => this.handelChange}
              value={this.state.filterBeast}
              id="displaySelected"
              defaultValue="Choose..."
            >
              <option value="0">All</option>
              <option value="1">1 Horen</option>
              <option value="2">2 Horen</option>
              <option value="3">3 Horen</option>
              <option value="100">100 Horen</option>
            </Form.Select>
          </Form.Group>
        </Form>
        <Row xs={1} md={3} className="g-4">
          {data.map((item, idx) => (
            <Col k={idx}>
              <HorendBeastC
                title={item.title}
                imageUrl={item.image_url}
                description={item.description}
                displayModal={this.props.displayModal}
                data={item}
              />
              <SelectedBeast
                title={item.title}
                imageUrl={item.image_url}
                description={item.description}
              />
            </Col>
          ))}
        </Row>
      </>
    );
  }
}
export default Main;
