import React from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import HorendBeastC from "./HornedBeastFi";

class SelectedBeast extends React.Component {
  render() {
    return (
      <>
        <Modal show={this.props.show} onHide={this.props.handelClose}>
          <Modal.Header closeButton>
            <Modal.Title>{this.props.selectedBeast.title} </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img src={this.props.selectedBeast.imageUrl} width="460" />
          </Modal.Body>
          <p>{this.props.description}</p>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.props.handelClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}
export default SelectedBeast;
