import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import data from "./data.json";

class Forms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numberOfHoren: 0,
      allHoren: data,
    };
  }
  // filterHorns() {
  //   if (this.state.numberOfHoren === 0) {
  //     this.state.allHoren = data;
  //   }

  //   console.log("Click happened");
  // }

  handleSelect(e) {
    e.preventDefault();
    this.setState({
      numberOfHoren: e.target.value,
    });
  }

  filterBeastByhornNumber = (e) => {
    const hornNumber = parseInt(e.target.value);
    let filteredData = data;
    if (hornNumber) {
      filteredData = data.filter((item) => item.horns === hornNumber);
    }
    this.setState({ allBeast: filteredData });
  };

  render() {
    return (
      <>
        <br />
        <br />
        <br />
        <br />
      </>
    );
  }
}
export default Forms;
