import React from "react";
import Modal from "react-bootstrap/Modal";
class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
    };
  }

  handelForm(e) {
    e.preventDefault();
    let userInput = e.props.horns;
    this.setState({
      userInput: userInput,
    });
  }
  openModal() {
    this.setState((prevState) => ({ show: !prevState.show }));
  }
  closeModal(e) {
    this.setState({ show: false });
  }

  render() {
    return (
      <>
        <form onSubmit={this.handelForm}>
          <label>
            <p>how many hourns you want to show ?</p>
            <select>
              <option>1 Horen</option>
              <option>2 Horen</option>
              <option>3 Horen</option>
              <option>4 Horen</option>
              <option>100 Horen</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>

        <Modal show={this.handelForm} onHide={this.handelForm}>
          <Modal.Header closeButton onClick={(e) => this.closeModal(e)}>
            <Modal.Title>{this.props.title} </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img src={this.props.imageUrl} width="460" alt={this.props.title} />
          </Modal.Body>
          <p>{this.props.description}</p>
          <Modal.Footer>
            <Button variant="secondary" onClick={(e) => this.closeModal(e)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}
export default Form;
