import "./App.css";
import "./component/Header";
import FooterRights from "./component/footer";
import H1header from "./component/Header";

import Main from "./component/main";
import Form from "./component/Form";
import React from "react";
import data from "./component/data.json";
class App extends React.Component {
  constructor(props) {
    this.state = {
      allBeast: data,
      selectedBeast: {},
      showModal: false,
    };
  }
  displayModal = (beast) => {
    let result = this.state.allBeast.find((item) => item.title === beast.title);
    this.setState({
      showModal: true,
      selectedBeast: result,
    });
  };
  handleClose = () => {
    this.setState({
      showModal: false,
    });
  };
  filterBeastByHornNumber = (e) => {
    const hornNumber = parseInt(e.target.value);
    let filteredData = data;
    if (hornNumber) {
      filteredData = data.filter((item) => item.horn === hornNumber);
    }
    this.setState({ allbeast: filteredData });
  };
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <H1header />

          <Form />
          <Main
            allBeast={this.state.allBeast}
            displayModal={this.displayModal}
            filterBeastByHornNumber={this.filterBeastByHornNumber}
          />
          <selectedBeast show={this.state.showModal} />

          <FooterRights />
        </header>
      </div>
    );
  }
}
export default App;
