import React from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

class SelectedBeast extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      openModal: true,
      closeModal: true,
    };
  }

  renderModal = () => {
    this.setState({ openModal: this.state.openModal });
  };
  hideModal = () => {
    this.setState({ closeModal: this.state.closeModal });
  };

  render() {
    return (
      <>
        <Button variant="primary" onClick={this.state.openModal}>
          Launch demo modal
        </Button>

        <Modal show={this.state.openModal} onHide={this.state.closeModal}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.state.closeModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}
export default SelectedBeast;
