import React from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

class Forms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      show: false,
    };
  }

  handelForm(e) {
    e.preventDefault();
    let userInput = e.props.horns;
    this.setState({
      userInput: userInput,
    });
  }
  openHo() {
    this.setState((prevState) => ({ show: !prevState.show }));
  }
  closeModal(e) {
    this.setState({ show: false });
  }

  render() {
    return (
      <>
        <Form>
          <Form.Group>
            <Form.label htmlFor="numberOfHoren">
              {" "}
              select the horned beast you want to show
            </Form.label>
            <Form.Select id="numberOfHoren">
              <option value="hb1">1 Horen</option>
              <option value="hb2">2 Horen</option>
              <option value="hb3">3 Horen</option>
              <option value="hb4">4 Horen</option>
              <option value="hb100">100 Horen</option>

              <br />

              <Modal
                size="lg"
                show={this.state.show}
                onHide={this.state.closeModal}
                aria-labelledby="example-modal-sizes-title-lg"
              >
                <Modal.Header closeButton>
                  <Modal.Title id="example-modal-sizes-title-lg">
                    Large Modal
                  </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  {this.props.title}
                  <img
                    src={this.props.imageUrl}
                    width="460"
                    alt={this.props.title}
                  />
                  <p>{this.props.description}</p>
                </Modal.Body>
              </Modal>
            </Form.Select>
          </Form.Group>
        </Form>
        <br />
        <br />
        <br />
      </>
    );
  }
}
export default Forms;
