import React from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      show: false,
    };
  }

  handelForm(e) {
    e.preventDefault();
    let userInput = e.props.horns;
    this.setState({
      userInput: userInput,
    });
  }
  openHo() {
    this.setState((prevState) => ({ show: !prevState.show }));
  }
  closeModal(e) {
    this.setState({ show: false });
  }

  render() {
    return (
      <>
        <form onSubmit={this.handelForm}>
          <label>
            <p>how many hourns you want to show ?</p>
            <select>
              <option>1 Horen</option>
              <option>2 Horen</option>
              <option>3 Horen</option>
              <option>4 Horen</option>
              <option>100 Horen</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>

        <Button onClick={() => setSmShow(true)} className="me-2">
          Small modal
        </Button>
        <Button onClick={() => setLgShow(true)}>Large modal</Button>
        <Modal
          size="lg"
          show={this.state.show}
          onHide={this.state.closeModal}
          aria-labelledby="example-modal-sizes-title-lg"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-lg">
              Large Modal
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>...</Modal.Body>
        </Modal>
      </>
    );
  }
}
export default Form;
