import React from "react";
import HorendBeastC from "./HornedBeastFi";
import data from "./data.json";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import SelectedBeast from "./SelectedBeast";
import Form from "react-bootstrap/Form";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.setState = {
      allBeast: data,
      selectedBeast: {},
      showModal: false,
      filterBeast: 0,
      beast: "",
    };
  }

  handelFilterData = (e) => {
    e.preventDefault();
  };

  displayModal = (beast) => {
    let result = this.state.allBeast.find((item) => item.title === beast.title);
    this.setState({
      showModal: true,
      selectedBeast: result,
    });
  };
  handleClose = () => {
    this.setState({
      showModal: false,
      selectedBeast: data,
    });
  };

  handelChange = (event) => {
    this.setState({
      filterBeast: event.target.value,
    });
  };
  render() {
    let finalFilter = [];
    if (this.state.filterBeast)
      return (
        <>
          <Form>
            <Form.Group controlId="formGridState">
              <Form.Label htmlFor="numberOfHoren">
                select the horned beast you want to show
              </Form.Label>
              <Form.Select
                onChange={(e) => this.filterData}
                id="displaySelected"
                defaultValue="Choose..."
              >
                <option value="All">1 Horen</option>
                <option value="1">1 Horen</option>
                <option value="2">2 Horen</option>
                <option value="3">3 Horen</option>
                <option value="100">100 Horen</option>
              </Form.Select>
            </Form.Group>
          </Form>
          <Row xs={1} md={3} className="g-4">
            {data.map((item) => (
              <Col>
                <HorendBeastC
                  title={item.title}
                  imageUrl={item.image_url}
                  description={item.description}
                  displayModal={this.props.displayModal}
                  data={item}
                />
                <SelectedBeast
                  title={item.title}
                  imageUrl={item.image_url}
                  description={item.description}
                />
              </Col>
            ))}
          </Row>
        </>
      );
  }
}
export default Main;
