import React from "react";

class HorendBeastC extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
    };
  }
  handleClick = () => {
    this.state({ counter: this.state.counter + 1 });
  };
}
export default HorendBeastC;
