import React from "react";
import HornedBeastFi from "./HornedBeastFi";
import data from "./data.json";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import SelectedBeast from "./SelectedBeast.js";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      filterBeast: "0",
      beasts: "",
    };
  }

  handleFilterBeasts = (e) => {
    e.preventDefault();
  };

  handleChange = (event) => {
    this.setState({
      filterBeast: event.target.value,
    });
  };

  render() {
    let finalData = [];
    if (this.state.filterBeast === "0") {
      finalData = data;
    } else {
      finalData = data.filter((card) => card.horns == this.state.filterBeast);
    }

    return (
      <>
        <form class="form-inline" onSubmit={this.handleFilterBeasts}>
          <label class="my-1 mr-2 " for="inlineFormCustomSelectPref">
            Preference
          </label>
          <select
            value={this.state.filterBeast}
            class="custom-select my-1 mr-sm-2"
            id="inlineFormCustomSelectPref"
            onChange={this.handleChange}
          >
            <option selected value="0">
              Choose...
            </option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="100">100</option>
          </select>
        </form>
        <Row xs={1} md={4} className="g-4">
          {finalData.map((card) => (
            <Col key={card.id}>
              <HornedBeastFi
                imageUrl={card.image_url}
                title={card.title}
                description={card.description}
                keyword={card.keyword}
                horns={card.horns}
                data={card}
              />
              <SelectedBeast
                imageUrl={card.image_url}
                title={card.title}
                description={card.description}
                keyword={card.keyword}
                horns={card.horns}
              />
            </Col>
          ))}
        </Row>
      </>
    );
  }
}

export default Main;
